sections.code:
/* 隐藏 */
div.fourth_column,
div.indexSuperise,
.recommend-right,
.csdn-toolbar,
.pulllog-box,
aside,
.tool-box,
.box-box-default,
.box-box-large,
.box-box-aways,
.meau-gotop-box,
.blog-content-box ~ div {
    display: none !important;
}

/* 居中显示文章内容 */
.container main {
    position: absolute;
    left: 15%;
    width: 70%;
}

/* 去掉“阅读更多”效果 */
.hide-article-box {
    display: none !important;
}
main div.blog-content-box article .article_content {
    height: auto !important;
}

/* 文章底部样式 */
main div.blog-content-box {
    margin-bottom: 10px;
}
main div.blog-content-box div.article-bar-bottom {
    padding-bottom: 0;
    margin-top: 10px;
    border-bottom: none;
}

/* 隐藏顶栏、底栏 */
.pic_container,
iframe,
.csdn-toolbar,
.news-nav,
.pub_fo {
    display: none !important;
}

/* 隐藏广告 */
#ad_pop,
.s-hl-content {
    display: none !important;
}

/* 隐藏推荐楼 */
.mod_topic_wrap {
    display: none;
}
.mod_topic_wrap.post {
    display: block;
}
.mod_fun_wrap,
.login {
    display: none !important;
}

#sidebar,
#share_toolbar_wrapper,
.crumb_ad,
.donate,
.entry-meta div,
.entry-meta h3,
.entry-meta .same-cat-post,
.hidden_components,
.post div {
    display: none;
}
.post .entry-cat,
.post .entry-banner,
.post .entry-content,
#post_download {
    display: block;
}
#container,
#content {
    width: 100%;
}
.post .entry-banner {
    margin: 0 auto;
}

/* 隐藏 */
.topTools,
.logom.fl,
.logor.fr,
.wrap,
.main.mb10.clearfix,
.main .w350.fr,
.article-content.border > div,
.art_xg,
.lbd.clearfix.mt5,
.jb51ewm,
#footer {
    display: none;
}

/* 显示文章内容 */
.art_desc.mt10,
#content {
    display: block !important;
    margin: 0 auto;
}
.bgf {
    background: transparent !important;
}

/* 文章内容居中 */
.main.clearfix.bgf {
    width: 100%;
}
.w750.fl {
    background: transparent !important;
    width: 70%;
    margin: 0 auto;
    padding: 0;
    float: none;
    left: 15%;
}

body,
#b_header {
    min-width: 900px !important;
}
#b_context,
#ev_talkbox_wrapper {
    display: none;
}
strong {
    color: #dd4b39;
    font-weight: normal;
}
#id_h {
    right: 0;
}
#b_header {
    padding-left: 200px;
}
#b_content {
    padding-left: 300px;
}
.b_deep,
#b_results .b_caption,
#b_results .b_snippet,
#b_results .b_attribution,
#b_results .b_factrow,
#b_results p,
#b_context .b_caption,
#b_context .b_snippet,
#b_context .b_attribution,
#b_context .b_factrow,
#b_context p,
#snct {
    line-height: 1.5;
}
